+                if(cachedValues.get(i) == value){
+                    sIndex = i;
+                    alreadyHasValue = true;
+                    break;
+                }
+            }
+             if(!alreadyHasValue) {
+                 cachedValues.add(value);
+                 cachedStrings.add(mFormat.format(value));
