diff --git a/MPChartLib/src/com/github/mikephil/charting/jobs/MoveViewJob.java b/MPChartLib/src/com/github/mikephil/charting/jobs/MoveViewJob.java
--- a/MPChartLib/src/com/github/mikephil/charting/jobs/MoveViewJob.java
+++ b/MPChartLib/src/com/github/mikephil/charting/jobs/MoveViewJob.java
@@ -0,0 +1,44 @@
+
+package com.github.mikephil.charting.jobs;
+
+import android.view.View;
+
+import com.github.mikephil.charting.utils.Transformer;
+import com.github.mikephil.charting.utils.ViewPortHandler;
+
+/**
+ * Runnable that is used for viewport modifications since they cannot be
+ * executed at any time. This can be used to delay the execution of viewport
+ * modifications until the onSizeChanged(...) method of the chartview is called.
+ * 
+ * @author Philipp Jahoda
+ */
+public class MoveViewJob implements Runnable {
+
+    protected ViewPortHandler mViewPortHandler;
+    protected float xIndex = 0f;
+    protected float yValue = 0f;
+    protected Transformer mTrans;
+    protected View view;
+
+    public MoveViewJob(ViewPortHandler viewPortHandler, float xIndex, float yValue,
+            Transformer trans, View v) {
+
+        this.mViewPortHandler = viewPortHandler;
+        this.xIndex = xIndex;
+        this.yValue = yValue;
+        this.mTrans = trans;
+        this.view = v;
+    }
+
+    @Override
+    public void run() {
+
+        float[] pts = new float[] {
+                xIndex, yValue
+        };
+
+        mTrans.pointValuesToPixel(pts);
+        mViewPortHandler.centerViewPort(pts, view);
+    }
+}

