+    LottieComposition composition = new LottieComposition();
+    composition.init(
+        new Rect(),
+        0f,
+        504.99f,
+        60f,
+        new ArrayList<>(),
+        new LongSparseArray<>(),
+        new HashMap<>(),
+        new HashMap<>(),
+        1f,
+        new SparseArrayCompat<>(),
+        new HashMap<>(),
+        new ArrayList<>(),
+        0,
+        0
