+    Keyframe<Float> keyframe2 = new Keyframe<>(composition, 321f, 300f, new LinearInterpolator(), 48f, 56f);
+    assertEquals(keyframe2.getStartProgress(), keyframe1.getEndProgress(), 0f);
+  }
+}

