+package com.airbnb.lottie.value;
+
+import android.graphics.PointF;
+import android.view.animation.Interpolator;
+
+import com.airbnb.lottie.utils.MiscUtils;
+
+public class LottieStaticInterpolatedPointValue extends LottieStaticInterpolatedValue<PointF> {
+  private final PointF point = new PointF();
+
+  public LottieStaticInterpolatedPointValue(PointF startValue, PointF endValue) {
+    super(startValue, endValue);
+  }
+
+  public LottieStaticInterpolatedPointValue(PointF startValue, PointF endValue,
+      Interpolator interpolator) {
+    super(startValue, endValue, interpolator);
+  }
+
+  @Override PointF interpolateValue(PointF startValue, PointF endValue, float progress) {
+    point.set(
+        MiscUtils.lerp(startValue.x, endValue.x, progress),
+        MiscUtils.lerp(startValue.y, endValue.y, progress)
+    );
+    return point;
+  }
+}

diff --git a/lottie/src/main/java/com/airbnb/lottie/value/LottieStaticInterpolatedValue.java b/lottie/src/main/java/com/airbnb/lottie/value/LottieStaticInterpolatedValue.java
--- a/lottie/src/main/java/com/airbnb/lottie/value/LottieStaticInterpolatedValue.java
+++ b/lottie/src/main/java/com/airbnb/lottie/value/LottieStaticInterpolatedValue.java
@@ -0,0 +1,29 @@
+package com.airbnb.lottie.value;
+
+import android.view.animation.Interpolator;
+import android.view.animation.LinearInterpolator;
+
+abstract class LottieStaticInterpolatedValue<T> extends LottieValueCallback<T> {
+
+  private final T startValue;
+  private final T endValue;
+  private final Interpolator interpolator;
+
+  LottieStaticInterpolatedValue(T startValue, T endValue) {
+    this(startValue, endValue, new LinearInterpolator());
+  }
+
+  LottieStaticInterpolatedValue(T startValue, T endValue, Interpolator interpolator) {
+    this.startValue = startValue;
+    this.endValue = endValue;
+    this.interpolator = interpolator;
+  }
+
+  @Override public T getValue(float startFrame, float endFrame, T startValue, T endValue,
+      float linearKeyframeProgress, float interpolatedKeyframeProgress, float overallProgress) {
+    float progress = interpolator.getInterpolation(overallProgress);
+    return interpolateValue(this.startValue, this.endValue, progress);
+  }
+
+  abstract T interpolateValue(T startValue, T endValue, float progress);
+}

