diff --git a/open-liberty/src/main/java/com/baeldung/openliberty/SystemReadinessCheck.java b/open-liberty/src/main/java/com/baeldung/openliberty/SystemReadinessCheck.java
--- a/open-liberty/src/main/java/com/baeldung/openliberty/SystemReadinessCheck.java
+++ b/open-liberty/src/main/java/com/baeldung/openliberty/SystemReadinessCheck.java
@@ -0,0 +1,32 @@
+package com.baeldung.openliberty;
+
+import javax.enterprise.context.ApplicationScoped;
+
+import javax.inject.Inject;
+import javax.inject.Provider;
+
+import org.eclipse.microprofile.config.inject.ConfigProperty;
+import org.eclipse.microprofile.health.Readiness;
+import org.eclipse.microprofile.health.HealthCheck;
+import org.eclipse.microprofile.health.HealthCheckResponse;
+import org.eclipse.microprofile.health.HealthCheckResponseBuilder;
+
+@Readiness
+@ApplicationScoped
+public class SystemReadinessCheck implements HealthCheck {
+
+    @Inject
+    @ConfigProperty(name = "io_openliberty_guides_system_inMaintenance")
+    Provider<String> inMaintenance;
+
+    @Override
+    public HealthCheckResponse call() {
+        HealthCheckResponseBuilder builder = HealthCheckResponse.named(
+                SystemResource.class.getSimpleName() + " readiness check");
+        if (inMaintenance != null && inMaintenance.get().equalsIgnoreCase("true")) {
+            return builder.withData("services", "not available").down().build();
+        }
+        return builder.withData("services", "available").up().build();
+    }
+
+}

