+
+    public int getMilk() {
+        return milk;
+    }
+
+    public boolean isHerbs() {
+        return herbs;
+    }
+
+    public int getSugar() {
+        return sugar;
+    }
+
+    public int getTeaPowder() {
+        return teaPowder;
+    }
