+	public Iterable<Order> getOrders() {
+		return orderRepository.findAll();
+	}
+
+	@Override
+	public boolean registerOrder(Order order) {
+		if (order.getTotal() > 0)
+			orderRepository.save(order);
+		else
+			return false;
+		
+		return true;
+	}
