+
+            if (arr[i] > maxEndingHere + arr[i]) {
+                start = i;
+                maxEndingHere = arr[i];
+            } else
+                maxEndingHere = maxEndingHere + arr[i];
+
+            if (maxSoFar < maxEndingHere) {
+                maxSoFar = maxEndingHere;
+                end = i;
+            }
+        }
+        System.out.println("Found Maximum Subarray between " + start + " and " + end);
+        return maxSoFar;
+    }
+}

