diff --git a/data-structures/src/main/java/com/baeldung/list/CircularLinkedList.java b/data-structures/src/main/java/com/baeldung/list/CircularLinkedList.java
--- a/data-structures/src/main/java/com/baeldung/list/CircularLinkedList.java
+++ b/data-structures/src/main/java/com/baeldung/list/CircularLinkedList.java
@@ -0,0 +1,90 @@
+package com.baeldung.list;
+
+import org.slf4j.Logger;
+import org.slf4j.LoggerFactory;
+
+public class CircularLinkedList {
+
+    final Logger LOGGER = LoggerFactory.getLogger(CircularLinkedList.class);
+
+    private Node head = null;
+    private Node tail = null;
+
+    public void addNode(int value) {
+
+        Node newNode = new Node(value);
+
+        if (head == null) {
+            head = newNode;
+        } else {
+            tail.nextNode = newNode;
+        }
+
+        tail = newNode;
+        tail.nextNode = head;
+    }
+
+    public boolean containsNode(int searchValue) {
+
+        Node currentNode = head;
+
+        if (head == null) {
+            return false;
+        } else {
+            do {
+                if (currentNode.value == searchValue) {
+                    return true;
+                }
+                currentNode = currentNode.nextNode;
+            } while (currentNode != head);
+            return false;
+        }
+    }
+
+    public void deleteNode(int valueToDelete) {
+
+        Node currentNode = head;
+
+        if (head != null) {
+            if (currentNode.value == valueToDelete) {
+                head = head.nextNode;
+                tail.nextNode = head;
+                currentNode = null;
+            } else {
+                do {
+                    Node nextNode = currentNode.nextNode;
+                    if (nextNode.value == valueToDelete) {
+                        currentNode.nextNode = nextNode.nextNode;
+                        nextNode = null;
+                        break;
+                    }
+                    currentNode = currentNode.nextNode;
+                } while (currentNode != head);
+            }
+        }
+    }
+
+    public void traverseList() {
+
+        Node currentNode = head;
+
+        if (head != null) {
+            do {
+                LOGGER.info(currentNode.value + " ");
+                currentNode = currentNode.nextNode;
+            } while (currentNode != head);
+        }
+    }
+
+}
+
+class Node {
+
+    int value;
+    Node nextNode;
+
+    public Node(int value) {
+        this.value = value;
+    }
