diff --git a/data-structures/src/test/java/com/baeldung/list/CircularLinkedListUnitTest.java b/data-structures/src/test/java/com/baeldung/list/CircularLinkedListUnitTest.java
--- a/data-structures/src/test/java/com/baeldung/list/CircularLinkedListUnitTest.java
