diff --git a/core-java-modules/core-java-date-operations/src/main/java/com/baeldung/datetime/AddSubtractDaysSkippingWeekendsUtils.java b/core-java-modules/core-java-date-operations/src/main/java/com/baeldung/datetime/AddSubtractDaysSkippingWeekendsUtils.java
--- a/core-java-modules/core-java-date-operations/src/main/java/com/baeldung/datetime/AddSubtractDaysSkippingWeekendsUtils.java
+++ b/core-java-modules/core-java-date-operations/src/main/java/com/baeldung/datetime/AddSubtractDaysSkippingWeekendsUtils.java
@@ -0,0 +1,21 @@
+package com.baeldung.datetime;
+
+import java.time.DayOfWeek;
+import java.time.LocalDate;
+
+public class AddSubtractDaysSkippingWeekendsUtils {
+
+    public static LocalDate addSubtractDaysSkippingWeekendsIterativeMethod(LocalDate date, int days) {
+        LocalDate result = date;
+        int addedDays = 0;
+        int absDays = Math.abs(days);
+        int day = (days > 0) ? 1 : -1;
+        while (addedDays < absDays) {
+            result = result.plusDays(day);
+            if (!(result.getDayOfWeek() == DayOfWeek.SATURDAY || result.getDayOfWeek() == DayOfWeek.SUNDAY)) {
+                ++addedDays;
+            }
+        }
+        return result;
+    }
+}

