+                                        String newLine = matcher.replaceAll(replacement + firstChar);
+
+                                        lines.add(newLine);
+                                    } else {
+                                        lines.add(line);
+                                    }
+
+                                }
+                            } finally {
+                                in.close();
+                            }
+
