+        } catch (Throwable e) {
+            Exceptions.throwIfFatal(e);
+            if (!d.isDisposed()) {
+                s.onError(e);
+            }
+            return;
+        }
+        if (!d.isDisposed()) {
+            s.onComplete();
+        }
+    }
+}

