+package rx.internal.operators;
+
+import rx.Observable.Operator;
+import rx.Subscriber;
+
+public class OperatorIgnoreElements<T> implements Operator<T, T> {
+
+    private static class Holder {
+        static final OperatorIgnoreElements<?> INSTANCE = new OperatorIgnoreElements<Object>();
+    }
+    
+    @SuppressWarnings("unchecked")
+    public static <T> OperatorIgnoreElements<T> instance() {
+        return (OperatorIgnoreElements<T>) Holder.INSTANCE;
+    }
+
+    private OperatorIgnoreElements() {
+
+    }
+
+    @Override
+    public Subscriber<? super T> call(final Subscriber<? super T> child) {
+        Subscriber<T> parent = new Subscriber<T>() {
+
+            @Override
+            public void onCompleted() {
+                child.onCompleted();
+            }
+
+            @Override
+            public void onError(Throwable e) {
+                child.onError(e);
+            }
+
+            @Override
+            public void onNext(T t) {
+                // ignore element
+            }
+
+        };
+        child.add(parent);
+        return parent;
+    }
+
+}

diff --git a/src/test/java/rx/internal/operators/OperatorIgnoreElementsTest.java b/src/test/java/rx/internal/operators/OperatorIgnoreElementsTest.java
